mapper.sql:
del_flag = #{productOrder.DEL_FLAG_NORMAL}


		AND state = #{productOrder.state}


		AND state != #{productOrder.PRODUCT_STATE_INIT}




		AND create_date BETWEEN #{beginDate} AND #{endDate}


		AND create_date >= #{beginDate}


		AND create_date <= #{endDate}

mapper.select:
SELECT
a.id AS "id",
a.goods_name AS "goodsName",
a.sales_amount AS "salesAmount",
a.visit_amount AS "visitAmount"
FROM goods a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.state != #{STATE_INVENTING}

		AND a.id = #{id}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort DESC
