mapper.select:
SELECT 

FROM goods_attr_value a

WHERE a.id = #{id}

SELECT 

FROM goods_attr_value a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.goods_id = #{goodsId}


		AND a.attr_id = #{attrId}




		ORDER BY ${page.orderBy}

SELECT 

FROM goods_attr_value a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE goods_attr_value SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE attr_id = #{attrId}
