mapper.select:
SELECT 

FROM goods a

WHERE a.id = #{id}

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id LIKE
		'%'||#{id}||'%'
		'%'+#{id}+'%'
		concat('%',#{id},'%')


		AND a.goods_name LIKE 
			'%'||#{goodsName}||'%'
			'%'+#{goodsName}+'%'
			concat('%',#{goodsName},'%')


		AND a.season = #{season}


		AND a.sex = #{sex}


		AND a.flag = #{flag}


		AND a.keywords LIKE 
			'%'||#{keywords}||'%'
			'%'+#{keywords}+'%'
			concat('%',#{keywords},'%')


		AND b.id = #{goodsCategory.id} OR b.parent_ids LIKE
			'%'||#{goodsCategory.id}||'%'
			'%'+#{goodsCategory.id}+'%'
			concat('%',#{goodsCategory.id},'%')



			AND a.price BETWEEN #{beginPrice} AND #{endPrice}



				AND a.price >= #{beginPrice}


				AND a.price <= #{endPrice}





			AND a.state = #{state}


			AND a.state != #{STATE_INVENTING}





		ORDER BY ${page.orderBy}


		ORDER BY a.sort DESC

SELECT
a.id AS "id",
a.goods_name AS "goodsName",
a.price AS "price",
a.src_price AS "srcPrice",
a.state AS "state",
a.thumb_img_url AS "thumbImgUrl",
a.sales_amount AS "salesAmount",
a.flag AS "flag"
FROM goods a

	a.del_flag = #{DEL_FLAG_NORMAL}

	AND ( a.goods_name LIKE
	'%'||#{keywords}||'%'
	'%'+#{keywords}+'%'
	concat('%',#{keywords},'%')
	OR a.keywords LIKE
	'%'||#{keywords}||'%'
	'%'+#{keywords}+'%'
	concat('%',#{keywords},'%')
	)

	AND a.state = #{state}

		AND a.flag = #{flag}



			AND a.goods_category_id = #{goodsCategory.id}


			AND a.goods_category_id IN

			#{categoryId}





			AND a.price BETWEEN #{beginPrice} AND #{endPrice}



				AND a.price >= #{beginPrice}


				AND a.price <= #{endPrice}






		ORDER BY ${page.orderBy}


		ORDER BY a.sort DESC

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE goods SET
visit_amount = visit_amount + 1
WHERE id = #{id}

UPDATE goods SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE goods_category_id = #{goodsCategory.id}
