mapper.select:
SELECT 

FROM goods a

WHERE a.id = #{id}

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id LIKE
		'%'||#{id}||'%'
		'%'+#{id}+'%'
		concat('%',#{id},'%')


		AND a.goods_name LIKE 
			'%'||#{goodsName}||'%'
			'%'+#{goodsName}+'%'
			concat('%',#{goodsName},'%')


		AND a.on_shelf_time BETWEEN #{beginOnShelfTime} AND #{endOnShelfTime}


		AND a.season = #{season}


		AND a.sex = #{sex}


		AND a.keywords LIKE 
			'%'||#{keywords}||'%'
			'%'+#{keywords}+'%'
			concat('%',#{keywords},'%')


		AND a.goods_category_id LIKE 
			'%'||#{goodsCategory.id}
			'%'+#{goodsCategory.id}
			concat('%',#{goodsCategory.id})


		AND a.price BETWEEN #{beginPrice} AND #{endPrice}


		AND a.src_price BETWEEN #{beginSrcPrice} AND #{endSrcPrice}



			AND a.state = #{state}


			AND a.state != #{STATE_INVENTING}





		ORDER BY ${page.orderBy}

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE goods SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE goods_category_id = #{goodsCategory.id}
