mapper.select:
SELECT 

FROM search_record a

WHERE a.id = #{id}

SELECT 

FROM search_record a


	a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.search_time = #{searchTime,jdbcType=DATE}


			AND a.search_time BETWEEN #{beginSearchTime} AND #{endSearchTime}


			AND a.search_time >= #{beginSearchTime}


			AND a.search_time <= #{endSearchTime}



		AND a.content = #{content}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM search_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE search_record SET
	amount = amount + 1,
	remarks = #{remarks},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE search_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
