mapper.select:
SELECT 

FROM member_address a

WHERE a.id = #{id}

SELECT 

FROM member_address a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.member_id = #{memberId}


		AND a.default_flag = #{defaultFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM member_address a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE member_address SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
