mapper.select:
SELECT 

FROM orders_details a

WHERE a.id = #{id}

SELECT 

FROM orders_details a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.orders_id = #{ordersId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM orders_details a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE orders_details SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE orders_id = #{ordersId}
